    }

    renderCOmponend() {

        this.props.children.find(route => {
            if(route.type === Route && route.props.path === this.state.currentLocation) {
                return route   
            }
        })

    }

    render() {
        return (
            <div>
                {this.renderCOmponend()}
            </div>
        )
    }
}
