import React from 'react'
import {Route} from './link'
export  class Router extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            currentLocation: window.location.pathname
        }
    }

    componentDidMount() {
        // subscribe history change && change state of current path
    }

    renderCOmponend() {
        this.props.children.find(route => {
            console.log(route.props)
            if(route.type === Route && route.props.path === this.state.currentLocation) {
                return (<route {...route.props} />)   
            }
        })
    }

    render() {
        return (
            <div>
                {this.renderCOmponend()}
            </div>
        )
    }
}
