export  class Route extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      currentLocation: window.history.location
    }
    console.log("currentLocation >>>"+ this.state.currentLocation )
  }

  render() {
    console.log(history.location.pathname)
    console.log(this.props.path)
    if(this.props.path === history.location.pathname)
      console.log('heloooo')
    return (
      <div>
      { this.props.path === history.location.pathname &&
        this.props.children
      }
      </div>
    )
  }
}
export  class Link extends React.Component {

  transition = event => {
    event.preventDefault();

    window.history.pushState(null, null, event.currentTarget.pathname)
    // render component 
    const component = mapPath(event.currentTarget.pathname)
    // const parentComponent = this._reactInternalInstance._currentElement._owner._instanc
    console.log(component)
    // ReactDOM.render(component, document.getElementsByTagName('Route'));
  };


  render() {
    return (
      <a 
        href={this.props.to}
        onClick={this.transition}
        className= {this.props.className}
      >
        {this.props.children}
      </a>

    )
  }
}
