import React from "react";
import {connect} from "react-redux";
import {getTasks} from "../../actions/task";
import {Task} from "./task";

class TaskList extends React.Component {

  componentDidMount() {
    //load user tasks
    this.props.getTasks();
  }
  
  compaltedTasks = () => {
    if (this.props.tasks)
      return this.props.tasks.map(
        task => !task.done && <Task key={task._id} task={task} />
      );
  };

  unCompaltedTasks = () => {
    if (this.props.tasks)
      return this.props.tasks.map(
        task => task.done && <Task key={task._id} task={task} />
      );
  };
  
  render() {
    const loadingTask = this.props.loadingTask;
    return (
      <div>
        <div className="row justify-content-md-center">
          <div className="card text-white bg-dark col-md-9 mb-3">
            <div className="card-header">
              <h1>Tasks</h1>
              {loadingTask && (
                <img
                  src="data:image/gif;base64,"
                  alt="wait loging"
                />
              )}
            </div>
            <div className="card-body">
              {this.compaltedTasks()}
            </div>
          </div>
        </div>
        <div className="row justify-content-md-center">
          <div className="card text-white bg-dark col-md-9 mb-3">
            <div className="card-header">
              <h1>Complated Tasks</h1>
              {loadingTask && (
                <img
                  src="data:image/gif;base64,"
                  alt="wait loging"
                />
              )}
            </div>
            <div className="card-body">
              {this.unCompaltedTasks()}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  const { loadingTask, tasks, error } = state.task;
  return {
    loadingTask,
    tasks,
    error
  };
}

function mapDispatchToProps(dispatch) {
  return {
    getTasks: () => dispatch(getTasks())
  };
}

const connectedTaskList = connect(
  mapStateToProps,
  mapDispatchToProps
)(TaskList);
export { connectedTaskList as TaskList };
